import { CART_ADD_ITEM, CART_REMOVE_ITEM } from '../contents/cartConstents'
import axios from 'axios'


export const addToCart = (id, qty) => async (dispatch, getState) => {
  const { data } = await axios.get(`/api/products/${id}`);

  // 计算新的数量
  const itemInCart = getState().cart.cartItems.find((x) => x.product === id);
  const currentQty = itemInCart ? itemInCart.qty : 0;
  const newQty = currentQty + qty;

  // 用更新后的数量派发动作
  dispatch({
    type: CART_ADD_ITEM,
    payload: {
      product: data._id,
      name: data.name,
      image: data.image,
      price: data.price,
      countInStock: data.stock,
      qty: newQty > data.stock ? data.stock : newQty, // 不要超过库存量
    },
  });

  localStorage.setItem('cartItems', JSON.stringify(getState().cart.cartItems));
};
