import { CART_ADD_ITEM, CART_REMOVE_ITEM } from '../contents/cartConstents'

//购物车reducer
export const cartReducer = (state = { cartItems: [] }, action) => {
  switch (action.type) {
    // 在cartReducer中
    case CART_ADD_ITEM: {
      const {item, fromProductPage} = action.payload;

      const existItem = state.cartItems.find(x => x.product === item.product);

      if (existItem) {
        // 如果是从产品页面添加，则累加数量；否则，设置为特定的数量
        const updatedQty = fromProductPage ? existItem.qty + item.qty : item.qty;
        return {
          ...state,
          cartItems: state.cartItems.map(x =>
            x.product === existItem.product ? { ...x, qty: updatedQty } : x
          ),
        };
      } else {
        // 如果商品不存在于购物车中，则添加新商品
        return {
          ...state,
          cartItems: [...state.cartItems, item],
        };
      }
    }


    default:
      return state
  }
}
