import React from 'react';
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import '../index.css';

const Header = () => {
  return (
    <header className='header'>
      <Navbar bg="lg" variant="light" expand="lg" collapseOnSelect >
        <Container>
          <Navbar.Brand href="/">
            <img
              src="/header.png"  
              alt="小酒馆"
              width="80"
              height="80"
            /> 
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav style={{ marginLeft: 'auto' }}> {/* className updated to 'ml-auto' for right aligning the nav items */}
              <Nav.Link href="/orders"> {/* Add your orders route here */}
                <i className="fas fa-box"></i>订单
              </Nav.Link>
              <Nav.Link href="/cart">
                <i className="fas fa-shopping-cart"></i>购物车
              </Nav.Link>
              <Nav.Link href="/login">
                <i className="fas fa-user"></i>登录
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </header>
  );
};

export default Header;
