import React, { useEffect, useRef } from 'react';
import './HomeScreen.css';

const HomeScreen = () => {
  // 创建一个ref来引用section-two的DOM元素
  const parallaxRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const scrolled = window.scrollY; // 获取当前滚动位置
      if (parallaxRef.current) {
        // 根据滚动位置调整背景位置，这里的0.5是滚动速度的系数
        // 背景移动速度会比页面滚动速度慢，从而产生视差效果
        parallaxRef.current.style.backgroundPositionY = `${scrolled * 0.5}px`;
      }
    };

    // 添加滚动事件监听器
    window.addEventListener('scroll', handleScroll);

    // 清理函数，在组件卸载时移除事件监听器
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="home-screen">
      <div className="section section-one">
        {/* ...第一部分的内容... */}
      </div>
      <div className="section section-two" ref={parallaxRef}>
        {/* ...第二部分的内容... */}
      </div>
      {/* ...更多部分... */}
    </div>
  );
};

export default HomeScreen;
